import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import possLetters from './possletters';
class Letter extends Component {
    constructor(props){
        super(props);
        this.state={
            bgColor: "primary",
            clicked: false
        };
        this.handleClick=this.handleClick.bind(this);
    };
    handleClick(){
      possLetters.value=possLetters.value.filter(x=>x!==this.props.value);
        this.setState({
          bgColor: "secondary",
          clicked: 'true'
        })
      };
    render(){
        return (
          <Button variant={this.state.bgColor} onClick={this.handleClick} disabled={this.state.clicked}>{this.props.value}</Button>
        );
    }
};
export default Letter;
