import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import ButtonToolbar from 'react-bootstrap/Button';
var alphabet="ABCDEFGHIJKLMNOPQRSTUVWXYZ";
class Letter extends Component {
    static index;
    constructor(props){
        index++;
        super(props);
        this.state={
            bgColor: "primary",
            letter: index,
            clicked: false
        };
        this.handleClick=this.handleClick.bind(this);
    };
    handleClick(){
        this.setState({
          bgColor: "secondary",
          letter: this.state.letter,
          clicked: 'true'
        })
      };
    getLetter(){
        return alphabet[this.state.letter];
    }
    render(){
        return (
          <Button variant={this.state.bgColor} onClick={this.handleClick} disabled={this.state.clicked}></Button>
        );
    }
}
export default Letter;
