import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
class Letter extends Component {
    setLet(i){
      this.state.letter=i;
    }
    constructor(props){
        super(props);
        this.state={
            bgColor: "primary",
            clicked: false
        };
        this.handleClick=this.handleClick.bind(this);
        this.getLetter=this.getLetter.bind(this);
    };
    handleClick(){
        this.setState({
          bgColor: "secondary",
          clicked: 'true',
          letter: this.state.letter
        })
      };
    getLetter(){
      return this.state.letter;
    }
    render(){
        return (
          <Button variant={this.state.bgColor} onClick={this.handleClick} disabled={this.state.clicked}>{this.props.value}</Button>
        );
    }
};
export default Letter;
