import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import ButtonToolbar from 'react-bootstrap/Button';
class Letter extends Component {
    constructor(props, letr){
        super(props);
        this.state={
            bgColor: "primary",
            letter: letr,
            clicked: false
        };
        this.handleClick=this.handleClick.bind(this);
    };
    handleClick(){
        this.setState({
          bgColor: "secondary",
          letter: this.state.letter,
          clicked: 'true'
        })
      };
    render(){
        return (
          <Button variant={this.state.bgColor} onClick={this.handleClick} disabled={this.state.clicked}>A</Button>
        );
    }
}
export default Letter;
