import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';
import ButtonToolbar from 'react-bootstrap/Button';
class Letter extends Component {
    constructor(props){
        super(props);
        this.state={
            bgColor: "primary",
            clicked: false
        };
        this.handleClick=this.changeColor.bind(this);
    };
    getInitialState(){
        return {
          bgColor: 'red',
          letter: 'A'
        }
      };
    handleClick(){
        this.setState({
          bgColor: 'blue',
          letter: 'A'
        })
      };
    getLetter(){
          return this.state.letter;
    };
    changeColor(event){
        if(this.state.clicked===false){
            this.setState({
                clicked: true
            });
        }
    }
    render(){
        return (
          <Button variant={this.state.colorUp} onClick={this.changeColor} disabled={this.state.clicked}>A</Button>
        );
    }
}
export default Letter;
