import React, { Component } from 'react';
import './hangman.css';
import './name';
import h0 from './images/h0.png';
import h1 from './images/h1.png';
import h2 from './images/h2.png';
import h3 from './images/h3.png';
import h4 from './images/h4.png';
import h5 from './images/h5.png';
import h6 from './images/h6.png';
import { randomName } from './name';
import Button from 'react-bootstrap/Button';
class Hangman extends Component{
    static defaultProps={
        triesLeft: 6,
        images: [h0, h1, h2, h3, h4, h5, h6]
    }
    constructor(props){
        super(props);
        this.state={
            mistake: 0,
            guessed: new Set([]),
            answer: randomName()
        }
    }
    generateButtons(){
        return "abcdefghijklmnopqrstuvwxyz".split("").map(letter => (
            <Button variant="primary">{letter}</Button>
        ));
    }
    guessedWord(){
        return this.state.answer.split("").map(letter => this.state.guessed.has(letter.toUpperCase())||this.state.guessed.has(letter.toLowerCase()) ? letter : " _ ");
    }
    render(){
        const gameOver=this.state.mistake>=this.props.triesLeft;
        return (
            <div>
                <h1>Marvel Character Hangman</h1>
                <div id="tries" className="float-right">Tries: {this.state.mistake}</div>
                <div className="text-center">
                    <p id="word">{gameOver ? this.state.answer:this.guessedWord()}</p>
                    </div>
                <div className="text-center">
                    <img src={this.props.images[this.state.mistake]} alt=""/>
                </div>
            </div>
        );
    }
}
export default Hangman;
